import request from 'superagent';
const urlApi = require('../config/config-react').urlApi;

function path(path) {
    return urlApi.join(path);
}

async function fetchDataBooks() {
    let data = [];
    request.get(path('/get-books')).then(msg => {
        data = msg.body;
    });
    return data;
}

async function insertDataBooks(title, author) {
    let data = [];
    request.get(path(`/save/${title}/${author}`)).then(msg => {
        data = msg.body;
    });
    return data;
}

module.exports = {
    fetchDataBooks,
    insertDataBooks
}
